mapper.select:
SELECT sequence, title, file_url, info_sequence, id, register_date 
FROM tbl_qrcode

	LIMIT #{pg.startPoint}, #{pg.endPoint}

SELECT sequence, title, file_url, info_sequence, id, register_date,update_date
FROM tbl_qrcode
WHERE sequence = #{sequence}

mapper.update:
UPDATE tbl_qrcode
title = #{title}, info_sequence = #{info_sequence}, file_url = #{file_url}, update_date = sysdate()
WHERE sequence = #{sequence}
